mapper.select:
select username, userid, email, joindate, region, admin
from ssafy_member
where userid = #{userid}

mapper.update:
update ssafy_member
set delflag = 1
where userid = #{userid}

update ssafy_member
username = #{username}, userpwd = #{userpwd}, email = #{email}, region = #{region},
where userid = #{userid}

mapper.select:
select count(userid)
from member
where userid = #{userid}

select userid, username, email, date_format(joindate, '%y.%m.%d') joindate, delflag, region
from ssafy_member
order by joindate desc;
